import React, { useState } from 'react';
import ChatWindow from './components/ChatWindow';
import Sidebar from './components/Sidebar';
import Header from './components/Header';
import './styles.css';

const chatHistories = {
  1: [
    { sender: 'bot', text: '안녕하세요, 무엇을 도와드릴까요?' },
    { sender: 'user', text: '이전 대화 내용이 궁금해요.' }
  ],
  2: [
    { sender: 'bot', text: '두 번째 대화입니다. 무엇을 도와드릴까요?' },
    { sender: 'user', text: '두 번째 대화에요.' }
  ]
};

function App() {
  const [isSidebarOpen, setSidebarOpen] = useState(true);
  const [currentChatId, setCurrentChatId] = useState(null); // 현재 선택된 채팅 ID
  const [messages, setMessages] = useState([]); // 현재 채팅 메시지들
  const [isNightMode, setNightMode] = useState(false); // 야간 모드 상태
  const [showInitialMessage, setShowInitialMessage] = useState(true); // 초기 메시지 상태

  const toggleSidebar = () => {
    setSidebarOpen(!isSidebarOpen);
  };

  const handleChatHistoryClick = (chatId) => {
    setCurrentChatId(chatId);
    setMessages(chatHistories[chatId] || []); // 선택된 채팅 내역 불러오기
    setShowInitialMessage(false); // 기존 채팅을 선택하면 초기 메시지 숨김
  };

  const handleSend = (message) => {
    const userMessage = { sender: 'user', text: message };
    const updatedMessages = [...messages, userMessage];

    setMessages(updatedMessages);
  };

  const startNewChat = () => {
    setMessages([]); // 새로운 대화 시작 시 메시지 초기화
    setCurrentChatId(null); // 현재 선택된 채팅 ID 초기화
    setShowInitialMessage(true); // 초기 메시지를 다시 보여줌
  };

  const toggleNightMode = () => {
    setNightMode(!isNightMode); // 야간 모드 토글
  };

  return (
    <div className={`App ${isSidebarOpen ? 'sidebar-open' : 'sidebar-closed'} ${isNightMode ? 'night-mode' : ''}`}>
      <Sidebar
        isOpen={isSidebarOpen}
        toggleSidebar={toggleSidebar}
        onChatHistoryClick={handleChatHistoryClick}
        onStartNewChat={startNewChat}
        onToggleNightMode={toggleNightMode}
        isNightMode={isNightMode}
      />
      <div className="main-content">
        <Header />
        <ChatWindow
          initialMessages={messages} // 초기 메시지로 선택된 대화 내역 전달
          onSend={handleSend}
          showInitialMessage={showInitialMessage} // 초기 메시지 표시 여부 전달
        />
      </div>
    </div>
  );
}

export default App;
