import React from 'react';
import { FiEdit } from 'react-icons/fi'; // 글쓰기 아이콘
import { BsMoon } from 'react-icons/bs'; // 달 아이콘

const Sidebar = ({ isOpen, toggleSidebar, onChatHistoryClick, onStartNewChat, onToggleNightMode, isNightMode }) => {
  return (
    <div className={`sidebar ${isOpen ? 'open' : 'closed'}`}>
      <button className="toggle-button" onClick={toggleSidebar}>
        {isOpen ? '<<' : '>>'}
      </button>
      {isOpen && (
        <>
          <ul>
            <li className="icon-button" onClick={onStartNewChat}>
              <FiEdit size={24} />
            </li> {/* 새로운 대화 시작 버튼 */}
            <li onClick={() => onChatHistoryClick(1)}>Chat History 1</li>
            <li onClick={() => onChatHistoryClick(2)}>Chat History 2</li>
          </ul>
          <div className="sidebar-bottom">
            <button className="icon-button" onClick={onToggleNightMode}>
              <BsMoon size={24} />
            </button>
          </div> {/* 야간 모드 버튼 */}
        </>
      )}
    </div>
  );
};

export default Sidebar;
