import React, { useState, useEffect, useRef } from 'react';
import Message from './Message';
import InputBox from './InputBox';

const ChatWindow = ({ initialMessages = [], onSend }) => {
  const [messages, setMessages] = useState(initialMessages);
  const [showInitialMessage, setShowInitialMessage] = useState(initialMessages.length === 0); // 초기 메시지 상태 관리
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    setMessages(initialMessages);
    if (initialMessages.length > 0) {
      setShowInitialMessage(false); // 대화 내역이 있으면 초기 메시지 숨김
    }
  }, [initialMessages]);

  const handleSend = async (message) => {
    if (showInitialMessage) {
      setShowInitialMessage(false); // 첫 메시지를 보낼 때 초기 메시지를 숨김
    }

    const userMessage = { sender: 'user', text: message };
    setMessages((prevMessages) => [...prevMessages, userMessage]);

    try {
      const response = await fetch('http://localhost:7860/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message }),
      });

      const data = await response.json();
      const botMessage = { sender: 'bot', text: data.reply };
      setMessages((prevMessages) => [...prevMessages, botMessage]);
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  return (
    <div className="chat-window">
      {showInitialMessage && (
        <div className="initial-message">
          <span className="greeting">안녕하세요,</span>
          <span className="subtext">무엇을 도와드릴까요?</span>
        </div>
      )}
      <div className="messages-container">
        {messages.map((msg, index) => (
          <Message key={index} sender={msg.sender} text={msg.text} />
        ))}
        <div ref={messagesEndRef} />
      </div>
      <InputBox onSend={handleSend} />
    </div>
  );
};

export default ChatWindow;
