import React, { useState } from 'react';
import ChatWindow from './components/ChatWindow';
import Sidebar from './components/Sidebar';
import Header from './components/Header';
import './styles.css';

const chatHistories = {
  1: [
    { sender: 'bot', text: '안녕하세요, 무엇을 도와드릴까요?' },
    { sender: 'user', text: '이전 대화 내용이 궁금해요.' }
  ],
  2: [
    { sender: 'bot', text: '두 번째 대화입니다. 무엇을 도와드릴까요?' },
    { sender: 'user', text: '두 번째 대화에요.' }
  ]
};

function App() {
  const [isSidebarOpen, setSidebarOpen] = useState(true);
  const [currentChatId, setCurrentChatId] = useState(null); // 현재 선택된 채팅 ID
  const [messages, setMessages] = useState([]); // 현재 채팅 메시지들

  const toggleSidebar = () => {
    setSidebarOpen(!isSidebarOpen);
  };

  const handleChatHistoryClick = (chatId) => {
    setCurrentChatId(chatId);
    setMessages(chatHistories[chatId] || []); // 선택된 채팅 내역 불러오기
  };

  const handleSend = (message) => {
    const userMessage = { sender: 'user', text: message };
    const updatedMessages = [...messages, userMessage];

    // 여기서는 단순히 메시지를 추가하는 동작만 구현
    setMessages(updatedMessages);

    // 실질적인 백엔드 통신이나 메시지 저장 기능을 구현하려면 추가적인 로직이 필요
  };

  return (
    <div className={`App ${isSidebarOpen ? 'sidebar-open' : 'sidebar-closed'}`}>
      <Sidebar
        isOpen={isSidebarOpen}
        toggleSidebar={toggleSidebar}
        onChatHistoryClick={handleChatHistoryClick}
      />
      <div className="main-content">
        <Header />
        <ChatWindow
          initialMessages={messages} // 초기 메시지로 선택된 대화 내역 전달
          onSend={handleSend}
        />
      </div>
    </div>
  );
}

export default App;
